import React from "react";
import Header from "./Components/Header";
// import Image from "./Components/Image";
import logo from "./img/logo.png";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      helpText: "help me",
    };

    this.inputClick = this.inputClick.bind(this);
  }

  render() {
    return (
      <div className="name">
        <Header title="look at me" name="opps" />
        <Header title="look at her" />
        <Header title="look at him" />
        <h1>{this.state.helpText}</h1>
        <input
          placeholder={this.state.helpText}
          onClick={this.state.inputClick}
          onMouseEnter={this.state.mouseOver}
        />
        <p>{this.state.helpText === "help me" ? "ok" : "no"}</p>

        <img src={logo} className="ttt" alt="apple" />
      </div>
    );
  }

  inputClick() {
    this.setState({ helpText: "changed" });
    console.log("clicked");
  }
  mouseOver() {
    console.log("over mouse");
  }
}

export default App;
